template_code.js_simplechart:

Ext.onReady(function() {
	var store = new Ext.data.JsonStore({
		fields: <?php echo isset($data[0]) ? json_encode(array_keys($data[0])) : '[]'; ?>,
		data: <?php echo json_encode($data); ?>

	});

	var cc = {
		width: <?php echo $config['width']; ?>,
		height: <?php echo $config['height']; ?>,
		renderTo: '<?php echo $uid; ?>',
		items: {
			xtype: '<?php echo $type; ?>',
			store: store,
			extraStyle: {
				legend: {
					display: 'bottom',
					padding: 5,
					font: {
						family: 'Tahoma',
						size: 11
					}
				}
			}
		}
	};

	Ext.apply(cc.items, <?php echo json_encode($config); ?>);

	new Ext.Panel(cc);

});

template_code.js_link2to:

Cronk.util.InterGridUtil.clickTOLink(
	'<?php echo $uid; ?>',
	'<?php echo $template; ?>',
	<?php echo json_encode($filterObj); ?>,
	'<?php echo $toTitle; ?>'
);

template_code.js_link2grid:

Cronk.util.InterGridUtil.clickGridLink(
	'<?php echo $uid; ?>',
	'<?php echo $template; ?>',
	<?php echo json_encode($filterObj); ?>,
	'<?php echo $gridTitle; ?>'
);
